column2:
configure terminal
    route-map [name] permit [number]
      set community {internet | no-advertise | no-export | local-as}
  router bgp [AS]
    neighbor [IP] route-map [name] {in | out}
    neighbor [IP] send-community

configure terminal
  interface [interface]
    ip ospf network {broadcast | non-broadcast | point-to-point | point-to-multipoint | point-to-multipoint non-broadcast}

configure terminal
  interface [interface]
    ip ospf priority {0-255}

configure terminal
  interface [interface]
    ospfv3 [number] priority {0-255}

configure terminal
  interface [interface]
    ospfv3 [number] network {broadcast | non-broadcast | point-to-point | point-to-multipoint | point-to-multipoint non-broadcast}

configure terminal
  interface [interface]
    ip access-group [name] {in | out}

configure terminal
  interface [interface]
    ipv6 traffic-filter [name] {in | out}

configure terminal  
  line [type] [number]
    access-class [name] {in | out}

configure terminal  
  line [type] [number]
    ipv6 access-class [name] {in | out}

configure terminal
  switchport port-security max [number]
  switchport port-security mac {[mac] | sticky}
  switchport port-security violation {protect | restrict | shutdown}
  switchport port-security

configure terminal
  interface [interface]
    spanning-tree vlan [VLAN] port-priority {0-255}

configure terminal
  interface [interface]
    glbp [number] load-balancing {host-dependent | round-robin | weighted}
