param ($path, $delimiter) 

# Import active directory module for running AD cmdlets 
Import-Module ActiveDirectory 

$ADUsers = Import-Csv $path -Delimiter $delimiter | Foreach-Object {
    $user = @{
        Name               = $_.Name
        GivenName          = $_.GivenName
        SurName            = $_.SurName
        SamAccountName     = $_.SamAccount
        AccountPassword    = (ConvertTo-secureString $_.Password -AsPlainText -Force)
        EmailAddress       = $_.Email
        OfficePhone        = $_.Phone
        City               = $_.City
        Title              = $_.Title
        Organization       = $_.Organization
        Path               = $_.Path
        HomeDirectory      = "\\FR-FILE\homes\%username%"
        HomeDrive          = "H:"
        ProfilePath        = "\\FR-FILE\profiles\%username%"
    }
    if (Get-ADUser -F { SamAccountName -eq "$user.SamAccountName" }) {
        Write-Warning "User already exists: $user"
    } 
    else {
        try {
            New-ADUser @user -ErrorAction Stop
        } catch {
            Write-Debug ([PSCustomObject]$user)    # output parameter info
            $_                                     # output the actual error
        }
    }
}
